mapper.insert:
 		/* 연차등록 추가 */
	 	INSERT INTO tb_annual_application
			(annual_application_code
			, cp_representative_code
			, super_admin_id
			, cate_annual_code
			, approval_request_code
			, emply_id
			, emply_name
			, annual_start_date
			, annual_end_date
			, use_date
			, `comment`
			, attach_file_name
			, reg_date
			, group_code
		)VALUES (
			(SELECT CONCAT('annual_application_code_',(SELECT(Max(cast(SUBSTRING_INDEX(annual_application_code,'_',-1) AS UNSIGNED)) +1)
		               from
		               tb_annual_application
		               sw)))
			, #{cpRepresentativeCode}
			, #{superAdminId}
			, #{cateAnnualCode}
			, #{approvalRequestCode}
			, #{emplyId}
			, #{emplyName}
			, #{annualStartDate}
			, #{annualEndDate}
			, #{useDate}
			, #{comment}
			, #{attachFileName}
			, NOW()
			, #{groupCode}
		);
